document.addEventListener("onRadicalMartExpressCheckoutError",function(event){if(event.detail){UIkit.notification(event.detail,{status:"danger"})}document.querySelectorAll('[radicalmart_express-checkout="openWindow"], [data-radicalmart_express-checkout="openWindow"]').forEach(function(button){button.removeAttribute("disabled")})});document.addEventListener("onRadicalMartExpressCheckoutBeforeOpenWindow",function(event){document.querySelectorAll('[radicalmart_express-checkout="openWindow"], [data-radicalmart_express-checkout="openWindow"]').forEach(function(button){button.setAttribute("disabled","")})});document.addEventListener("onRadicalMartExpressCheckoutAfterOpenForm",function(event){if(event.detail){UIkit.modal(document.querySelector("#RadicalMartExpressCheckoutModal")).show()}document.querySelectorAll('[radicalmart_express-checkout="openWindow"], [data-radicalmart_express-checkout="openWindow"]').forEach(function(button){button.removeAttribute("disabled")})});document.addEventListener("onRadicalMartExpressCheckoutFormError",function(event){if(event.detail&&event.detail.messages){event.detail.messages.forEach(function(message){UIkit.notification(message,{status:"danger"})})}});